 function get_row_end_pos(text)
 {
   i = 5;
  
   found = false;
   while(!found)
   {
     
     if(text[i - 5] + text[i -4] + text[i - 3] + text[i - 2] + text[i - 1] == "</tr>")
        found = true;
     else
       i ++;
     
     
   }
   
   
   return i;
   
 }
 


  function get_start_pos(text,start_pos,matcher)
 {
   i = start_pos;
   found = false;
   chars = matcher.length
   
   while(!found)
   {
     word = "";
     for(c = chars ; c > 0; c--){
      word += text[i - c];
     }
     
      if(word == matcher)
        found = true;
     else
       i --;
     
   }
   
   return i;
   
 }
 
  
