function getLeft(el) {
    var tmp = el.offsetLeft;
    el = el.offsetParent;
    while(el) {
       tmp += el.offsetLeft;
       el = el.offsetParent;
    }
    return tmp;
 }

function getTop(el) {
   var tmp = el.offsetTop;
   el = el.offsetParent;
   while(el) {
     tmp += el.offsetTop;
     el = el.offsetParent;
   }
   return tmp;
}


