function change_input_name(name)
{
  var new_name ="";
  for(char_index = 0 ; char_index < name.length ; char_index++)
    {
     if(name[char_index]  == "[" | name[char_index] == "]")
      {
       if(name[char_index] == "[")
         {
           new_name += "$";
         }
         else
          {
            new_name += "?";
          }
       }
       else
       {
        new_name += name[char_index];
       }
  }

return new_name;
}
